mapper.select:
select count(*) from  xry_comment a left join xry_course b on  a.obj_id=b.id left join xry_teacher c on a.obj_id = c.id
  left join xry_teacher d on a.user_id=d.id where 1=1

    and a.obj_id like #{params.courseId} and a.type = 0


    and a.obj_id like #{params.userId} and a.type = 1


    and a.type like #{params.type}


    and a.status like #{params.status}

select a.*,b.title as courseTitle, b.id as courseId, c.nickname,c.id as userId,d.real_name as realName,d.id as teacherId
    from  xry_comment a left join xry_course b on  a.obj_id=b.id left join xry_user c on a.obj_id = c.id
    left join xry_teacher d on a.user_id=d.id where 1=1

    and a.obj_id like #{params.courseId} and a.type = 0


    and a.obj_id like #{params.userId} and a.type = 1


    and a.type like #{params.type}


    and a.status like #{params.status}


    limit ${params.pageNo},${params.pageSize}
