mapper.select:
select count(*) from xry_teacher a left join xry_user b on a.user_id=b.id left join xry_organization c on a.org_id=c.id where 1=1

    and a.status = #{params.status}


    and a.real_name like #{params.realName}


    and b.phone like #{params.userPhone}


    and date(a.created) like #{params.created}


    and a.recommend = #{params.recommend}

select a.*,b.nickname,b.phone as userPhone,c.org_name as orgName, c.org_code as orgCode
from xry_teacher a left join xry_user b on a.user_id=b.id left join xry_organization c on a.org_id=c.id where
1=1

    and a.status = #{params.status}


    and a.real_name like #{params.realName}


    and b.phone like #{params.userPhone}


    and date(a.created) like #{params.created}


    and a.recommend = #{params.recommend}


    limit ${params.pageNo},${params.pageSize}

select xua.obj_id as teacherId, xt.*,xu.avatar as headImg from xry_user_attention xua left join xry_teacher xt on xua.obj_id = xt.id
left join xry_user xu on xt.user_id = xu.id where xua.user_id = #{params.userId} and xua.obj_type = 0 and xua.obj_status = 0
limit ${params.pageNo},${params.pageSize}
