mapper.select:
select a.*,b.title, b.image from xry_user_applicant a left join xry_course b on a.obj_id=b.id
  where a.user_id = #{params.userId} and a.obj_type = 0 and a.obj_status = 0


        and a.create_time between current_date()-7 and sysdate();


        and date_format(a.create_time, '%Y%m' ) = date_format(curdate() , '%Y%m' )






    limit ${params.pageNo},${params.pageSize}

mapper.delete:
delete from xry_user_applicant where id in

    #{id}

and user_id = #{params.userId}
